def gfx_to_image(gfx_str, output_file):
    # Convertimos la cadena en lista de líneas
    lines = [line for line in gfx_str.strip().splitlines() if line.strip()]
    height = len(lines)
    width = len(lines[0])
    img = Image.new('RGB', (width, height))
    pixels = img.load()

    for y, line in enumerate(lines):
        for x, c in enumerate(line):
            color_index = int(c, 16)
            pixels[x, y] = PALETTE[color_index]

    img.save(output_file)
    print(f"Imagen guardada en {output_file}")

if __name__ == "__main__":
    gfx_to_image(gfx_string, "../assets/sprites/spritesheet.png")